var AjaxAdmin = Class.create();
AjaxAdmin.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    setULSubteamBox: function(){

		//var sGroupSysId = this.getParameter('sGroupSysId');
		var sCurrentUserId = this.getParameter('sCurrentUserId');
		var sGroupSysId='';

		//var iCurrentState = this.getParameter('iCurrentState');
		var jsonObject={};
		var labels=[];
		var values=[];
		var oGr1 = new GlideRecord('x_58872_needit_admin_table');
		oGr1.orderBy('description');
		oGr1.query();
		while(oGr1.next()){

				labels.push(oGr1.description.toString());
				values.push(oGr1.sys_id.toString());

		}

		jsonObject.labels=labels;
		jsonObject.values=values;

		//jsonObject.state=iCurrentState.toString();
		return JSON.stringify(jsonObject);


	}
});
